import './App.css'
import { useState } from 'react';
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link} from "react-router-dom";
import { Navigate } from 'react-router-dom';
import LoginPage from './login';
import UserPage from './user';
import SignUpPage from './signup';
import { currUser } from './state.js';

function Footer()
{
  return (
    <footer class="bd-footer py-4 py-md-5 mt-5 bg-light">
      <div class="container py-4 py-md-5 px-4 px-md-3 border border-dark">
        <div class="row">
          <h5>Made by Leni Broady and Kate Kelsey</h5>
        </div>
      </div>
    </footer>
  );
}

function Navbar(props)
{
  return ( 
    <nav class="navbar navbar-expand-lg bg-secondary">
      <div class="container-fluid">
        <a class="navbar-brand" href="#">Navbar</a>
        <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNavAltMarkup" aria-controls="navbarNavAltMarkup" aria-expanded="false" aria-label="Toggle navigation">
          <span class="navbar-toggler-icon"></span>
        </button>
        <div class="collapse navbar-collapse" id="navbarNavAltMarkup">
          <div class="navbar-nav">
            <Link class="nav-link active" aria-current="page" to="./user">Account</Link>
            <Link class="nav-link" to="./login">Login</Link>
            <Link class="nav-link" to="./signup">Sign Up</Link>
          </div>
          {props.navUser === undefined?"":<div>Current User: {props.navUser}</div>}
        </div>
      </div>
    </nav>
  );
}

function App() 
{
  const [toggle, setToggle] = useState(true);
  const [navUser, setNavUser] = useState();
  const toggleState = () => setToggle(!toggle);
  const updateUser = () => 
  {
    setNavUser(currUser.username)
  }

  function AppRouter () 
  {
    return (
      <Router>
        <Navbar navUser={navUser}>

        </Navbar>
        <div className="container">
          <Routes>
            <Route exact path="/" element={<Navigate to="/login"/>}/>
            <Route exact path="/login" element={<LoginPage toggleState={toggleState} updateUser={updateUser}/>}/>
            <Route exact path="/signup" element={<SignUpPage toggleState={toggleState} updateUser={updateUser}/>}/>
            <Route exact path="/user" element={<UserPage updateUser={updateUser}/>}/>
          </Routes>
        </div>
      </Router>
    )
  }
  return (   
    <body> 
      <AppRouter/>

      <script src="https://cdn.jsdelivr.net/npm/bootstrap@5.2.2/dist/js/bootstrap.bundle.min.js" integrity="sha384-OERcA2EqjJCMA+/3y+gxIOqMEjwtxJY7qPCqsdltbNJuaOe923+mo//f6V8Qbsw3" crossorigin="anonymous"></script>
      {Footer()};
    </body>
  );
}

export default App;
