import { useState } from 'react';
import { Email, Phone, Username } from '../components';
import {allUsers, currUser} from '../state';


export default function UserPage(props){
  const [currentInput, setInputs] = useState({});
  const handleChange = (event) => 
  {
    const{name, value} = event.target;
    setInputs(values => ({...values, [name]: value}))
  };

  const handleSubmit = (event) => 
  {
    event.preventDefault();
    for (var counter = 0; counter < allUsers.length; counter++)
    {
      if(allUsers[counter].username === currUser.username)
      {
        if(currentInput.username !== undefined){
          allUsers[counter].username = currentInput.username;
          currUser.username = currentInput.username;
        }

        if(currentInput.email !== undefined){
          allUsers[counter].email = currentInput.email;
          currUser.email = currentInput.email;
        }
        
        if(currentInput.phone !== undefined){
          allUsers[counter].phone = currentInput.phone;
          currUser.phone = currentInput.phone;
        }
        
        currUser.password = allUsers[counter].password;
      }
      props.updateUser();
    }
      
  }

  {
    return (
      <body>
        <h1>Update Account</h1>

        <div class="container bg-white">

          {/* <!-- Adapted from bootstrap tutorial at https://getbootstrap.com/docs/5.2/forms/validation/ --> */}
          <form class="row g-3" onSubmit={handleSubmit}>
            
            {/* <!-- Username --> */}
            <Username handleChange={handleChange} username={currentInput.username} default={currUser.username} required={true}/>


            {/* <!-- Email --> */}
            <Email handleChange={handleChange} email={currentInput.email} default={currUser.email} required={true}/>

            {/* <!-- Phone Number --> */}
            <Phone handleChange={handleChange} phone={currentInput.phone} default={currUser.phone} required={true}/>

            {/* <!-- Sign Up --> */}
            <div class="col-12">
              <button class="btn btn-primary" type="submit">Update</button>
            </div>  
          </form>
        </div>
      </body>
    );
  }
}
