import { useState } from 'react';
import 
{
  useNavigate
} from "react-router-dom";
import { Email, Password, Username } from '../components';
import {allUsers, currUser} from '../state';


export default function LoginPage(props)
{
  const [currentInput, setInputs] = useState({});
  const{username, email, password} = currentInput;
  const navigate = useNavigate();
  const [user, setUsername] = useState('')

  const handleChange = (event) => 
  {
    const{name, value} = event.target;
    setInputs(values => ({...values, [name]: value}))
  }

  const handleSubmit = (event) => 
  {
    event.preventDefault();

    if ( (currentInput.username === '' || currentInput.username == undefined) && (currentInput.email === '' || currentInput.email == undefined)) //edge case
    {
      alert ("\nMust fill in either username or email.")
    }
    else
    {
      for (var counter = 0; counter < allUsers.length; counter++)
      {
        if ( ( (allUsers[counter].username === currentInput.username) || (allUsers[counter].email === currentInput.email) ) 
        && allUsers[counter].password == currentInput.password1)
        {
            props.toggleState();
            currUser.username = allUsers[counter].username;
            currUser.password = allUsers[counter].password;
            currUser.email = allUsers[counter].email;
            currUser.phone = allUsers[counter].phone;
            props.updateUser();
            return navigate('/user');
        }
      }
      alert ("\nUser not found.")
    }
    return (false);
  }

  return (
    <body>
      <h1>Please Login Below</h1>
        
      <div class="container bg-white">

        <form class="row g-3 needs-validation" onSubmit={handleSubmit}>

          {/* Username */}
          <Username handleChange={handleChange} username={currentInput.username} default={''} required={false}/>

          {/* Email */}
          <Email handleChange={handleChange} email={currentInput.email} default={''} required={false}/>

          {/* Password */}
          <Password handleChange={handleChange} password1={currentInput.password1} default={''} required={true} />
          <div class="text-center">
            <button class="btn btn-primary" type="submit">Sign In</button>
          </div>
        </form>
      </div>
    </body>
  );
}
