import { useState } from 'react';
import 
{
  useNavigate
} from "react-router-dom";
import { Email, Password, PasswordVerify, Phone, Username } from '../components';
import {signUpUser} from '../state';
import {allUsers, currUser} from '../state';

export default function SignUpPage(props)
{
  //adapted in part from https://sebhastian.com/handlechange-react/
  const [inputs, setInputs] = useState({});

  const{username, email, password1, password2, phone} = inputs;

  const navigate = useNavigate();
  
  const handleChange = (event) => 
  {
    const{name, value} = event.target;
    setInputs(values => ({...values, [name]: value}))
  }

  const handleSubmit = (event) => 
  {
    event.preventDefault();
    
    if (inputs.password1 !== inputs.password2) 
    {
      alert ("\nPassword did not match: Please try again.")
      return false;
    }
    else
    {
      signUpUser(inputs.username, inputs.password1, inputs.email, inputs.phone);
      props.toggleState();
      let last = allUsers.length-1;
      currUser.username = allUsers[last].username;
      currUser.password = allUsers[last].password;
      currUser.email = allUsers[last].email;
      currUser.phone = allUsers[last].phone;
      props.updateUser();
      return navigate('/user')     
    }
  }
  return (
    <body>
      <h1>Welcome! Please enter information to make an account.</h1>

      <div class="container bg-white">
      
      {/* <!-- Adapted from bootstrap tutorial at https://getbootstrap.com/docs/5.2/forms/validation/--> */}

      {/* <!-- checks to make sure passwords are the same --> */}
      <form class="row g-3 needs-validation" onSubmit={handleSubmit}> 

        {/* <!-- Username --> */}
        <Username handleChange={handleChange} username={inputs.username} default={''} required={true}/>

        {/* <!-- Email -->*/}
        <Email handleChange={handleChange} email={inputs.email} default={''} required={true}/>

        {/* Password and verify password code adapted from https://www.w3schools.com/react/react_forms.asp#:~:text=In%20HTML%2C%20form%20data%20is,handlers%20in%20the%20onChange%20attribute. */}
        {/* <!-- Password --> */}
        <Password handleChange={handleChange} password1={inputs.password1} default={''} required={true}/>

        {/* <!-- Verify Password --> */}
        <PasswordVerify handleChange={handleChange} password2={inputs.password2} default={''} required={true}/>

        {/* <!-- Phone Number --> */}
        <Phone handleChange={handleChange} phone={inputs.phone} default={''} required={true}/>

        {/* <!-- Sign Up --> */}
        <div class="col-12">
          <button class="btn btn-primary" type="submit">Sign Up</button>
        </div>
      
      </form> 
      </div>
    </body>
  );
}
